Behaviors.Macro:
(L:LEVER_MAN_OVRD) @ManOverrideTreshhold > if{
	(I:XMLVAR_ThrottlePos_Disconnected_YAxis) (>L:XMLVAR_ThrottlePos_YAxis)
} els{
	(A:GENERAL ENG THROTTLE LEVER POSITION:1, percent) (>L:XMLVAR_ThrottlePos_YAxis)
}

l0 0 <= if{ @ReverseSpeedFactor (>O:ReverseSpeedFactor) } els{ 1 (>O:ReverseSpeedFactor) }

(L:LEVER_MAN_OVRD) @ManOverrideTreshhold > if{
	(I:XMLVAR_ThrottlePos_Disconnected_YAxis) @ThrottleMovement (O:ReverseSpeedFactor) * - -25 max (>I:XMLVAR_ThrottlePos_Disconnected_YAxis)
} els{
	l0 @ThrottleMovement16k (O:ReverseSpeedFactor) * - (>K:THROTTLE1_SET)
}

l0 0 < if{ @ReverseSpeedFactor (>O:ReverseSpeedFactor) } els{ 1 (>O:ReverseSpeedFactor) }

(L:LEVER_MAN_OVRD) @ManOverrideTreshhold > if{
	(I:XMLVAR_ThrottlePos_Disconnected_YAxis) @ThrottleMovement (O:ReverseSpeedFactor) * + 100 min (>I:XMLVAR_ThrottlePos_Disconnected_YAxis)
} els{
	l0 @ThrottleMovement16k (O:ReverseSpeedFactor) * + (>K:THROTTLE1_SET)
}

l1 (L:XMLVAR_ThrottlePos_YAxis) - abs l2 < if{
	l1 (>L:XMLVAR_ThrottlePos_YAxis)
} els{
	l1 (L:XMLVAR_ThrottlePos_YAxis) - 0 < if{
		(L:XMLVAR_ThrottlePos_YAxis) l2 - (>L:XMLVAR_ThrottlePos_YAxis)
	} els{ l1 (L:XMLVAR_ThrottlePos_YAxis) - 0 > if{
		(L:XMLVAR_ThrottlePos_YAxis) l2 + (>L:XMLVAR_ThrottlePos_YAxis)
	} }
}

l0 (L:XMLVAR_ThrottlePos_XAxis) - abs @AutoMoveSpeedProp < if{
	l0 (>L:XMLVAR_ThrottlePos_XAxis)
} els{
	l0 (L:XMLVAR_ThrottlePos_XAxis) - 0 < if{
		(L:XMLVAR_ThrottlePos_XAxis) @AutoMoveSpeedProp - (>L:XMLVAR_ThrottlePos_XAxis)
		@ConditionPosIdleHI (>L:XMLVAR_ThrottlePos_YAxis)
	} els{ l0 (L:XMLVAR_ThrottlePos_XAxis) - 0 > if{
		(L:XMLVAR_ThrottlePos_XAxis) @AutoMoveSpeedProp + (>L:XMLVAR_ThrottlePos_XAxis)
		@ThrottleValPropLeverChange (>L:XMLVAR_ThrottlePos_YAxis)
	} }
}

UseTemplate.ANIM_CODE:
	(*If Manual Override is more then its Threshold, then using XMLVAR_ThrottlePos_Disconnected_XAxis instead of the SimVars to drive the animation (as the throttle SimVar is then controlled by the Man Override)*)
	(L:LEVER_MAN_OVRD) @ManOverrideTreshhold > if{
		(I:XMLVAR_ThrottlePos_Disconnected_XAxis) sp0
		(I:XMLVAR_ThrottlePos_Disconnected_YAxis) sp1
	} els{
		(A:GENERAL ENG PROPELLER LEVER POSITION:1, percent)
		sp0

		(A:GENERAL ENG THROTTLE LEVER POSITION:1, percent)
		sp1

		l0 @PosIsFeathered @IsOnPos if{
			(A:GENERAL ENG FUEL VALVE:1, Bool) ! l1 0 > and if{
				@OpenFuelValve
			} els{
				(A:GENERAL ENG FUEL VALVE:1, Bool) l1 0 < and if{
					@CloseFuelValve
				}
			}
			l1 0.5 * @ConditionPosIdleHI @ConditionPosCutoff - 0.5 * (A:GENERAL ENG FUEL VALVE:1, Bool) * + sp1
		}
	}

(*Movement Speed is different depending on which side of the throttle we are*)
(L:XMLVAR_ThrottlePos_XAxis) @PosIsFeathered @IsOnPos if{
	@AutoMoveSpeedCondition sp2
} els{
	@AutoMoveSpeed sp2
}

(O:IsInit) 0 == if{
	1 (>O:IsInit)
	l0 (>L:XMLVAR_ThrottlePos_XAxis)
	l1 (>L:XMLVAR_ThrottlePos_YAxis)
} els{
	(*Instead of letting the animation do the smooth, the exact value is smoothed here which ensures we take the proper path instead of going through the model*)
	(L:XMLVAR_ThrottlePos_XAxis) l0 != if{
		(L:XMLVAR_ThrottlePos_YAxis) @ThrottleValPropLeverChange @IsOnPos
		@IsOnConditionIdleHIPos or
		if{
			@SmoothPropMovement
		} els{
			(L:XMLVAR_ThrottlePos_XAxis) @PosIsFeathered @IsOnPos if{
				@ConditionPosIdleHI sp1
			} els{
				@ThrottleValPropLeverChange sp1
			}
			@SmoothThrottleMovement
		}
	} els{
		@SmoothThrottleMovement
	}
}

(L:XMLVAR_ThrottlePos_XAxis) @PosIsFeathered @IsOnPos if{
	(*Display value is at most ConditionPosIdleHI, then scaled between ConditionPosIdleLO and ConditionPosIdleHI*)
	(L:XMLVAR_ThrottlePos_YAxis) @ConditionPosIdleHI min @ConditionPosCutoff - @ConditionPosIdleHI @ConditionPosCutoff - / @PosTransition * sp3
} els{
	(L:XMLVAR_ThrottlePos_XAxis) @PosIsUnFeathered @IsOnPos if{
		(L:XMLVAR_ThrottlePos_YAxis) 0 >= if{
			(L:XMLVAR_ThrottlePos_YAxis) 100 + sp3
		} els{
			(L:XMLVAR_ThrottlePos_YAxis) (A:THROTTLE LOWER LIMIT, percent) / -100 * 100 + sp3
		}
	} els{
		@PosTransition  sp3
	}	
}
l3

UseTemplate.UPDATE_CODE:
(L:XMLVAR_ThrottlePos_XAxis) @PosIsFeathered @IsOnPos if{
	(A:GENERAL ENG THROTTLE LEVER POSITION:1, position 16k) @ConditionPosIdleHI16k > if{
		(*If on the Feathered side but above max value - unfeather*)
		@SetToUnFeatheredSim
	}
	(A:GENERAL ENG THROTTLE LEVER POSITION:1, position 16k) @ConditionPosCutoff16k < if{
		(*If on the Feathered side but above max value - unfeather*)
		@SetToConditionPosCutoff_Sim
		@ConditionPosCutoff (>L:XMLVAR_ThrottlePos_YAxis)
	}
}
(A:GENERAL ENG PROPELLER LEVER POSITION:1, position 16k) @PosIsUnFeathered16k @IsOnPos ! 
(A:GENERAL ENG PROPELLER LEVER POSITION:1, position 16k) @PosIsFeathered16k @IsOnPos ! and if{
	(*Ensure the Prop position doesn't stay stuck in between expected values. This Update needs to be at a low frequency else it may interfere with the AutoStart*)
	(A:GENERAL ENG PROPELLER LEVER POSITION:1, position 16k) 0 > if{
		@SetToUnFeatheredSim
	} els{
		@SetToFeatheredSim
	}
}

UseTemplate.CALLBACKCODE:
(A:GENERAL ENG THROTTLE LEVER POSITION:1, position 16k) sp0
(A:GENERAL ENG PROPELLER LEVER POSITION:1, position 16k) sp1
(M:Event) 'LeftSingle' scmp 0 == if{
	(M:RelativeY) (>O:PrevY, Number)
	(M:RelativeX) (>O:PrevX, Number)

	(O:GateStep) 1 == if{ 2 (>O:GateStep) }
	(O:NeedsToRelease) 1 == if{ 0 (>O:NeedsToRelease) }
	0 (>O:IsLimitedToAny)
	0 (>O:IsLimitedToCutoff)
	0 (>O:IsLimitedToIdleLO)
	0 (>O:IsLimitedToIdleHI)
}
(M:Event) 'LeftDrag' scmp 0 == if{
	(*Storing Variables, which are then called using ShortHand Macros*)
	(O:PrevX) (M:RelativeX) - 1000 * @XSpeed * sp2
	(O:PrevY) (M:RelativeY) - 1000 * @YSpeed * sp3
	(L:XMLVAR_ThrottlePos_XAxis) @PosIsFeathered @IsOnPos sp4
	(L:XMLVAR_ThrottlePos_XAxis) @PosIsUnFeathered @IsOnPos sp5
	@IsOnConditionCutoffPos sp6
	@IsOnConditionIdleLOPos sp7
	@IsOnConditionIdleHIPos sp8	

	(L:XMLVAR_ThrottlePos_YAxis) @ThrottleValPropLeverChange - abs 7.5 < @IsUnFeathered and sp9
	@IsFeathered @IsOnIdleHI and sp10

	@dMoveX abs @dMoveY abs >
	@IsAtPropJunctionInThrust 
	@IsAtPropJunctionInCondition or and
	if{
		(*If X Movement is Greater than Y Movement and Throttle is at Junction - horizontal movement is allowed*)
		@IsAtPropJunctionInThrust
		@dMoveX -@PropSideDragMin < and if{
				@SetToFeathered
		} els{
			@IsAtPropJunctionInCondition
			@dMoveX @PropSideDragMin > and if{
				@SetToUnFeathered
			}
		}
	} els{
		(*UnFeathered Movement - restricted by a gate before reverse which forces the user to release the LMButton*)
		@IsUnFeathered if{
			@dMoveY 0 > if{
					@IsAtReverseGate if{
					@SetToGateReverse
					@HandleGateDown
				} els{ 
					@ResetGate
					@MoveThrottleDown 
				}
			} els{
					@ResetGate
					@MoveThrottleUp
			}
		} els{
			(*Feathered movement - can only move by one notch without releasing*)
			@dMoveY -@ThrottleDragMin < if{
				@IsOnCutoff if{
					@SetToConditionPosIdleLo
					@SetLimitedToCutoff
				} els{ @IsOnIdleLO if{
					@SetToConditionPosIdleHI
					@SetLimitedToIdleLO
				} }
			} els{ @dMoveY @ThrottleDragMin > if{
						@IsOnIdleHI if{
							@SetToConditionPosIdleLO
							@SetLimitedToIdleHI
						} els{ @IsOnIdleLO if{
							@SetToConditionPosCutoff
							@SetLimitedToIdleLO
						}
					}
				}
			}
		}
	}

	(M:RelativeX) (>O:PrevX, Number)
	(M:RelativeY) (>O:PrevY, Number)
}

UseTemplate.UP_CODE:
(O:IsThrottleDisconnected) ! 
(L:LEVER_MAN_OVRD) @ManOverrideTreshhold > and if{
	1 (>O:IsThrottleDisconnected)
	(A:GENERAL ENG PROPELLER LEVER POSITION:1, percent) (>I:XMLVAR_ThrottlePos_Disconnected_XAxis)
	(A:GENERAL ENG THROTTLE LEVER POSITION:1, percent) (>I:XMLVAR_ThrottlePos_Disconnected_YAxis)
}

(O:IsThrottleDisconnected) if{
	@SetToUnFeatheredSim
	(L:LEVER_MAN_OVRD) @ManOverrideTreshhold - 100 @ManOverrideTreshhold - / 100 * @ConvertTo16k (>K:THROTTLE1_SET)
}

